mapper.select:

        SELECT sf.uniquename AS region, f.uniquename AS feature, fl.fmin as fmin, fl.fmax as fmax, fl.phase as phase, fl.strand as strand
		  FROM feature f
		  JOIN featureloc fl ON f.feature_id = fl.feature_id 
		  JOIN feature sf ON fl.srcfeature_id = sf.feature_id
		WHERE f.uniquename in #{item} 


		  AND sf.uniquename = #{region}

SELECT f.uniquename as uniqueName, f.name as name, synonym.name as synonym, cvterm.name as synonymtype, feature_synonym.is_current FROM feature f
JOIN feature_synonym ON f.feature_id = feature_synonym.feature_id JOIN synonym ON feature_synonym.synonym_id = synonym.synonym_id JOIN cvterm ON synonym.type_id = cvterm.cvterm_id
JOIN featureloc fl ON fl.feature_id = f.feature_id AND srcfeature_id=(SELECT feature_id FROM feature WHERE uniquename= #{region})
WHERE synonym.name ~* #{term}
WHERE synonym.name = #{term}

SELECT feature.uniquename as uniqueName, feature.name as name, synonym.name as synonym, cvterm.name as type, feature_synonym.is_current as is_current FROM feature_synonym JOIN feature ON feature.feature_id = feature_synonym.feature_id JOIN synonym ON feature_synonym.synonym_id = synonym.synonym_id JOIN cvterm ON synonym.type_id = cvterm.cvterm_id WHERE feature.uniquename IN #{item}
AND cvterm.name IN #{item}
UNION SELECT feature.uniquename as uniqueName, feature.name as name, synonym.name as synonym, cvterm.name as type, feature_synonym.is_current as is_current FROM feature_synonym JOIN feature ON feature.feature_id = feature_synonym.feature_id JOIN synonym ON feature_synonym.synonym_id = synonym.synonym_id JOIN cvterm ON synonym.type_id = cvterm.cvterm_id WHERE synonym.name IN #{item}
AND cvterm.name IN #{item}
