mapper.select:

        SELECT sf.uniquename AS region, f.uniquename AS uniqueName, f.name as name, fl.fmin as fmin, fl.fmax as fmax, fl.phase as phase, fl.strand as strand
		  FROM feature f
		  JOIN featureloc fl ON f.feature_id = fl.feature_id 
		  JOIN feature sf ON fl.srcfeature_id = sf.feature_id
		WHERE f.uniquename in #{item} 


		  AND sf.uniquename = #{region}


SELECT f.uniquename as uniqueName, f.name as name
FROM feature f





      WHERE f.uniquename ~* #{term} or f.name ~* #{term} 


      WHERE f.uniquename = #{term}  or f.name = #{term}

SELECT f.uniquename as uniqueName, f.name as name, synonym.name as synonym, cvterm.name as type, feature_synonym.is_current as is_current
FROM feature f
JOIN feature_synonym ON f.feature_id = feature_synonym.feature_id JOIN synonym ON feature_synonym.synonym_id = synonym.synonym_id JOIN cvterm ON synonym.type_id = cvterm.cvterm_id
WHERE synonym.name ~* #{term}
WHERE synonym.name = #{term}

SELECT feature.uniquename as uniqueName, feature.name as name, synonym.name as synonym, cvterm.name as type, feature_synonym.is_current as is_current FROM feature_synonym JOIN feature ON feature.feature_id = feature_synonym.feature_id JOIN synonym ON feature_synonym.synonym_id = synonym.synonym_id JOIN cvterm ON synonym.type_id = cvterm.cvterm_id WHERE feature.uniquename IN #{item}
AND cvterm.name IN #{item}
UNION SELECT feature.uniquename as uniqueName, feature.name as name, synonym.name as synonym, cvterm.name as type, feature_synonym.is_current as is_current FROM feature_synonym JOIN feature ON feature.feature_id = feature_synonym.feature_id JOIN synonym ON feature_synonym.synonym_id = synonym.synonym_id JOIN cvterm ON synonym.type_id = cvterm.cvterm_id WHERE synonym.name IN #{item}
AND cvterm.name IN #{item}

SELECT f.uniquename as uniqueName, f.name as name, fc.feature_cvterm_id as feature_cvterm_id, fc.is_not as is_not, d.accession, f.uniquename as feature, ct.name as cvterm, c.name as cv, fcp.value as prop, fcpct.name as type_name, fcpc.name as type_cv FROM feature f JOIN feature_cvterm fc ON f.feature_id = fc.feature_id JOIN cvterm ct ON fc.cvterm_id = ct.cvterm_id JOIN cv c ON c.cv_id = ct.cv_id
AND c.name IN #{item}
JOIN dbxref d ON ct.dbxref_id = d.dbxref_id LEFT OUTER JOIN feature_cvtermprop fcp on fc.feature_cvterm_id = fcp.feature_cvterm_id LEFT OUTER JOIN cvterm fcpct on fcp.type_id = fcpct.cvterm_id LEFT OUTER JOIN cv fcpc ON fcpct.cv_id = fcpc.cv_id
WHERE f.uniquename in #{item}

SELECT f.uniquename as uniqueName, f.name as name, orthof.uniquename as ortho, orthof.name as orthoname, orthof.feature_id as ortho_feature_id, orthotype.name as orthotype, relationshiptype.name as relationship, o.genus, o.species, op.value as taxonID
FROM feature f JOIN feature_relationship fr ON f.feature_id = fr.subject_id AND fr.type_id in (select cvterm.cvterm_id from cvterm join cv on cvterm.cv_id = cvterm.cv_id where cv.name = 'sequence' and (cvterm.name = 'orthologous_to' or cvterm.name = 'paralogous_to')) JOIN feature orthof ON fr.object_id = orthof.feature_id JOIN cvterm orthotype ON orthof.type_id = orthotype.cvterm_id JOIN cvterm relationshiptype ON fr.type_id = relationshiptype.cvterm_id LEFT JOIN organism o on orthof.organism_id = o.organism_id LEFT JOIN organismprop op on o.organism_id = op.organism_id AND op.type_id = (select cvterm.cvterm_id from cvterm join cv on cvterm.cv_id = cvterm.cv_id where cv.name = 'genedb_misc' and cvterm.name = 'taxonId')
WHERE f.uniquename in #{item}

SELECT
f.uniquename as uniqueName, f.name as name,
fp.value as value, fp.rank as rank,
ct.name as type_name, c.name as type_cv
FROM featureprop fp JOIN feature f ON fp.feature_id = f.feature_id JOIN cvterm ct ON fp.type_id = ct.cvterm_id JOIN cv c ON ct.cv_id = c.cv_id
WHERE fp.value ~* #{value}
WHERE fp.value = #{value}
AND ct.name = #{type}

SELECT f.uniquename as uniqueName, f.name as name, ct.name as type_name, c.name as type_cv, fc.is_not, d.accession as type_db_accession, fc.feature_cvterm_id as feature_cvterm_id
FROM feature f JOIN feature_cvterm fc ON f.feature_id = fc.feature_id JOIN cvterm ct ON fc.cvterm_id = ct.cvterm_id JOIN dbxref d ON ct.dbxref_id = d.dbxref_id
JOIN cv c ON c.cv_id = ct.cv_id
AND c.name = #{cv}
WHERE ct.name ~* #{cvterm}
WHERE ct.name = #{cvterm}

SELECT feature_id, organism_id, uniqueName, name FROM feature f



WHERE f.timelastmodified > #{date}


  AND f.organism_id = #{organism_id}
